import React, { useEffect } from 'react'
import styled from 'styled-components'
import { useGlobalContext } from '../../context/globalContext';
import History from '../../History/History';
import { InnerLayout } from '../../styles/Layouts';
import { dollar } from '../../utils/Icons';
import Chart from '../Chart/Chart';

function Dashboard() {
    const { totalExpenses, incomes, expenses, totalIncome, totalBalance, getIncomes, getExpenses } = useGlobalContext();

    useEffect(() => {
        getIncomes();
        getExpenses();
    }, [getIncomes, getExpenses]);

    return (
        <DashboardStyled>
            <div className="chart-container">
                <Chart />
            </div>
            <div className="stats-con">
                <div className="chart-con">
                    <div className="amount-con">
                        <div className="income">
                            <h2>Total Income</h2>
                            <p>{dollar} {totalIncome()}</p>
                        </div>
                        <div className="expense">
                            <h2>Total Expense</h2>
                            <p>{dollar} {totalExpenses()}</p>
                        </div>
                        <div className="balance">
                            <h2>Total Balance</h2>
                            <p>{dollar} {totalBalance()}</p>
                        </div>
                    </div>
                </div>
                <div className="history-con">
                    <History />
                </div>
            </div>
        </DashboardStyled>
    );
}

const DashboardStyled = styled.div`
    display: grid;
    grid-template-columns: 1fr 2fr;
    gap: 2rem;
    height: 100%; /* Ensure that the height is set to fill the container */

    .chart-container {
        display: flex;
        justify-content: center;
        align-items: center;
        height: 100%; /* This will vertically center the chart */
    }

    .stats-con {
        display: flex;
        flex-direction: column;
        gap: 2rem;

        .chart-con {
            /* Adjust height to take only the required space */
            .amount-con {
                display: grid;
                grid-template-columns: repeat(3, 1fr);
                gap: 2rem;
                margin-top: 2rem;

                .income, .expense, .balance {
                    background: #FCF6F9;
                    border: 2px solid #FFFFFF;
                    box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.06);
                    border-radius: 20px;
                    padding: 1rem;

                    h2 {
                        font-size: 2rem;
                        font-weight: bold;
                    }

                    p {
                        font-weight: 600;
                        font-size: 1.5rem;
                    }
                }

                .balance {
                    display: flex;
                    flex-direction: column;
                    justify-content: center;
                    align-items: center;

                    p {
                        color: var(--color-green);
                    }
                }
            }
        }

        .history-con {
            /* Adjust to fill remaining space below chart */
            h2 {
                margin: 1rem 0;
                display: flex;
                align-items: center;
                justify-content: space-between;
            }

            .salary-item {
                background: #FCF6F9;
                border: 2px solid #FFFFFF;
                box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.06);
                padding: 1rem;
                border-radius: 20px;
                display: flex;
                justify-content: space-between;
                align-items: center;

                p {
                    font-weight: 600;
                    font-size: 1.5rem;
                }
            }
        }
    }
`;
