import React, { useState } from 'react';
import styled from 'styled-components';
import Modal from '../Modal/Modal'; // Import your Modal component
import Button from '../Button/Button'; // Import the button
import { plus } from '../../utils/Icons'; // Icon for the add button

function Form() {
  const [showModal, setShowModal] = useState(false);

  const handleAddIncomeClick = () => {
    setShowModal(true);
    document.body.classList.add('modal-open'); // Apply blur effect to the background
  };

  const handleCloseModal = () => {
    setShowModal(false);
    document.body.classList.remove('modal-open'); // Remove blur effect
  };

  return (
    <FormStyled>
      <Button
        name={'Add Income'}
        icon={plus}
        bPad={'.8rem 1.6rem'}
        bRad={'30px'}
        bg={'var(--color-accent)'}
        color={'#fff'}
        onClick={handleAddIncomeClick}
      />

      {/* Modal for Add Income */}
      <Modal isOpen={showModal} onClose={handleCloseModal}>
        <h3>Add New Income</h3>
        <form>
          <input type="text" placeholder="Income Title" />
          <input type="number" placeholder="Amount" />
          {/* Add other fields here */}
          <button type="submit">Add Income</button>
        </form>
      </Modal>
    </FormStyled>
  );
}

const FormStyled = styled.div`
  /* Styles for the form and add button */
`;

export default Form;
