import React from 'react';
import styled, { keyframes } from 'styled-components';
import { closeIcon, Delete } from '../../utils/Icons';

const Modal = ({ showModal, setShowModal, children }) => {
    const handleClose = () => {
        setShowModal(false);
    };

    return (
        <>{showModal && (
            <ModalStyled>
                <div className="modal-content">
                    <div className="close-btn" onClick={handleClose}>
                        {Delete}
                    </div>
                    {children}
                </div>
            </ModalStyled>
        )}</>
    );
};

const blurBackground = keyframes`
    from {
        filter: blur(0);
    }
    to {
        filter: blur(8px);
    }
`;

const ModalStyled = styled.div`
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    display: flex;
    justify-content: center;
    align-items: center;
    background: rgba(0, 0, 0, 0.5);
    z-index: 10;
    animation: ${blurBackground} 0.5s forwards;

    .modal-content {
        background: linear-gradient(180deg, #F56692 100%, #F2994A 100%);
        padding: 2rem;
        border-radius: 10px;
        position: relative;
        z-index: 100;
    }

    .close-btn {
        position: absolute;
        top: 10px;
        right: 10px;
        cursor: pointer;
        font-size: 1.5rem;
        color: white;
    }

    /* Make the background behind modal blurred */
    body.modal-open {
        overflow: hidden;
        filter: blur(8px);
    }
`;

export default Modal;
