import React, { useState } from 'react';
import styled from 'styled-components';
import DatePicker from 'react-datepicker';
import "react-datepicker/dist/react-datepicker.css";
import { useGlobalContext } from '../../context/globalContext';
import Button from '../Button/Button';
import { plus } from '../../utils/Icons';

function Form() {
    const { addIncome, error, setError } = useGlobalContext();
    const [inputState, setInputState] = useState({
        title: '',
        amount: '',
        date: '',
        category: '',
        description: '',
        customCategory: ''
    });
    const [showCustomCategory, setShowCustomCategory] = useState(false);

    const { title, amount, date, category, description, customCategory } = inputState;

    const handleInput = name => e => {
        setInputState({ ...inputState, [name]: e.target.value });
        setError('');
    };

    const handleSubmit = e => {
        e.preventDefault();

        if (!amount || !date || !category) {
            setError('Amount, Date, and Category are required');
            return;
        }

        if (category === 'other' && !customCategory) {
            setError('Custom category is required when "Other" is selected');
            return;
        }

        addIncome(inputState);
        setInputState({
            title: '',
            amount: '',
            date: '',
            category: '',
            description: '',
            customCategory: ''
        });
    };

    return (
        <FormStyled onSubmit={handleSubmit}>
            {error && <p className='error'>{error}</p>}
            <div className="input-control">
                <input
                    type="text"
                    value={title}
                    name={'title'}
                    placeholder="Salary Title"
                    onChange={handleInput('title')}
                />
            </div>
            <div className="input-control">
                <input
                    value={amount}
                    type="text"
                    name={'amount'}
                    placeholder={'Salary Amount'}
                    onChange={handleInput('amount')}
                />
            </div>
            <div className="input-control">
                <DatePicker
                    id='date'
                    placeholderText='Enter A Date'
                    selected={date}
                    dateFormat="dd/MM/yyyy"
                    onChange={date => {
                        setInputState({ ...inputState, date });
                    }}
                />
            </div>
            <div className="selects input-control">
                <select required value={category} name="category" id="category" onChange={e => {
                    const value = e.target.value;
                    setInputState({ ...inputState, category: value });
                    if (value === 'other') {
                        setShowCustomCategory(true); // Show custom category input if 'Other' is selected
                    } else {
                        setShowCustomCategory(false); // Hide custom category input otherwise
                    }
                }}>
                    <option value="" disabled>Select Option</option>
                    <option value="salary">Salary</option>
                    <option value="freelancing">Freelancing</option>
                    <option value="investments">Investments</option>
                    <option value="stocks">Stocks</option>
                    <option value="bitcoin">Bitcoin</option>
                    <option value="bank">Bank Transfer</option>
                    <option value="youtube">YouTube</option>
                    <option value="other">Other</option>
                </select>
            </div>
            {showCustomCategory && (
                <div className="input-control">
                    <input
                        type="text"
                        value={customCategory}
                        name={'customCategory'}
                        placeholder={'Enter Custom Category'}
                        onChange={handleInput('customCategory')}
                    />
                </div>
            )}
            <div className="submit-btn">
                <Button
                    name={'Add Income'}
                    icon={plus}
                    bPad={'.8rem 1.6rem'}
                    bRad={'30px'}
                    bg={'#42AD00'}
                    color={'#fff'}
                />
            </div>
        </FormStyled>
    );
}

const FormStyled = styled.form`
  display: flex;
  flex-direction: column;
  gap: 2rem;
  input, textarea, select {
    font-family: inherit;
    font-size: inherit;
    outline: none;
    border: none;
    padding: .5rem 1rem;
    border-radius: 5px;
    border: 2px solid #ddd; /* Adjust form input border color */
    background: #f8f8f8; /* Adjust form input background color */
    color: black; /* Adjust form text color */
    &::placeholder {
      color: Black; /* Adjust placeholder text color */
    }
  }
  .input-control {
    input {
      width: 100%;
    }
  }
  .selects {
    display: flex;
    justify-content: flex-end;
    select {
      color: #333; /* Adjust select text color */
    }
    select:focus {
      color: #222; /* Adjust select focus text color */
    }
  }
`;

export default Form;
