import React from 'react';
import {
    Chart as ChartJs, 
    ArcElement,
    Tooltip,
    Legend,
} from 'chart.js';
import { Pie } from 'react-chartjs-2';
import styled from 'styled-components';
import { useGlobalContext } from '../../context/globalContext';

ChartJs.register(
    ArcElement,
    Tooltip,
    Legend
);

function CombinedPieChart() {
    const { incomes, expenses } = useGlobalContext();

    // Aggregate income and expense categories
    const categories = {};

    incomes.forEach(income => {
        categories[income.category] = (categories[income.category] || 0) + income.amount;
    });

    expenses.forEach(expense => {
        categories[expense.category] = (categories[expense.category] || 0) - expense.amount;
    });

    // Prepare data for the pie chart
    const data = {
        labels: Object.keys(categories),
        datasets: [
            {
                label: 'Income vs Expenses by Category',
                data: Object.values(categories).map(value => Math.abs(value)),
                backgroundColor: ['#4CAF50', '#FFEB3B', '#00BCD4', '#FF5722', '#9C27B0', '#03A9F4', '#8BC34A'],
                borderWidth: 1,
            },
        ],
    };

    return (
        <ChartStyled>
            <h2>Income vs Expenses by Category</h2>
            <Pie data={data} />
        </ChartStyled>
    );
}

const ChartStyled = styled.div`
    background: #FCF6F9;
    border: 2px solid #FFFFFF;
    box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.06);
    padding: 2rem;
    border-radius: 20px;
    width: 100%;
    max-width: 600px;
    margin: auto;
`;

export default CombinedPieChart;
