import React from 'react';
import {
    Chart as ChartJs, 
    ArcElement,
    Tooltip,
    Legend,
} from 'chart.js';
import { Pie } from 'react-chartjs-2';
import styled from 'styled-components';
import { useGlobalContext } from '../../context/globalContext';

ChartJs.register(
    ArcElement,
    Tooltip,
    Legend
);

function PieChart() {
    const { incomes, expenses } = useGlobalContext();

    // Aggregate income and expense categories
    const incomeCategories = incomes.reduce((acc, income) => {
        acc[income.category] = (acc[income.category] || 0) + income.amount;
        return acc;
    }, {});

    const expenseCategories = expenses.reduce((acc, expense) => {
        acc[expense.category] = (acc[expense.category] || 0) + expense.amount;
        return acc;
    }, {});

    // Prepare data for the pie chart
    const incomeData = {
        labels: Object.keys(incomeCategories),
        datasets: [
            {
                label: 'Income by Category',
                data: Object.values(incomeCategories),
                backgroundColor: ['#4CAF50', '#FFEB3B', '#00BCD4', '#FF5722', '#9C27B0', '#03A9F4', '#8BC34A'],
                borderWidth: 1,
            },
        ],
    };

    const expenseData = {
        labels: Object.keys(expenseCategories),
        datasets: [
            {
                label: 'Expenses by Category',
                data: Object.values(expenseCategories),
                backgroundColor: ['#E57373', '#F06292', '#BA68C8', '#9575CD', '#7986CB', '#64B5F6', '#4FC3F7'],
                borderWidth: 1,
            },
        ],
    };

    return (
        <ChartStyled>
            <h2>Income by Category</h2>
            <Pie data={incomeData} />
            <h2>Expenses by Category</h2>
            <Pie data={expenseData} />
        </ChartStyled>
    );
}

const ChartStyled = styled.div`
    background: #FCF6F9;
    border: 2px solid #FFFFFF;
    box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.06);
    padding: 2rem;
    border-radius: 20px;
    width: 100%;
    max-width: 600px;
    margin: auto;
`;

export default Chart;
