import React from 'react';
import styled from 'styled-components';
import avatar from '../../img/avatar.png';
import { signout } from '../../utils/Icons';
import { menuItems } from '../../utils/menuItems';

function Navigation({ active, setActive }) {
    const handleMenuItemClick = (id) => {
        setActive(id);
    };

    return (
        <>
            <Header>
                <div className="left-section">
                    <div className="user-con">
                        <img src={avatar} alt="User Avatar" />
                        <div className="text">
                            <h2>Mike</h2>
                            <p>Your Money</p>
                        </div>
                    </div>
                    <ul className="menu-items">
                        {menuItems.map((item) => (
                            <li
                                key={item.id}
                                onClick={() => handleMenuItemClick(item.id)}
                                className={active === item.id ? 'active' : ''}
                            >
                                {item.icon}
                                <span>{item.title}</span>
                            </li>
                        ))}
                    </ul>
                </div>
                <SignOut>
                    {signout} Sign Out
                </SignOut>
            </Header>
            <Footer>
                <ul className="menu-items">
                    {menuItems.map((item) => (
                        <li
                            key={item.id}
                            onClick={() => handleMenuItemClick(item.id)}
                            className={active === item.id ? 'active' : ''}
                        >
                            {item.icon}
                            <span>{item.title}</span>
                        </li>
                    ))}
                </ul>
            </Footer>
        </>
    );
}

const Header = styled.div`
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    background: black;
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 0 2rem;
    box-shadow: 0px 1px 17px rgba(0, 0, 0, 0.06);
    z-index: 1000;
    height: 80px;

    .left-section {
        display: flex;
        align-items: center;
        gap: 2rem; /* Adjust spacing between items as needed */

        .user-con {
            display: flex;
            align-items: center;
            gap: 1rem;

            img {
                width: 50px;
                height: 50px;
                border-radius: 50%;
                object-fit: cover;
                background: #fcf6f9;
                border: 2px solid #FFFFFF;
                padding: .2rem;
                box-shadow: 0px 1px 17px rgba(0, 0, 0, 0.06);
            }

            .text {
                h2 {
                    color: Wheat;
                }

                p {
                    color: white;
                }
            }
        }

        .menu-items {
            display: flex;
            gap: 1rem; /* Adjust spacing between menu items as needed */
            padding: 0;
            margin: 0;
            list-style: none;

            li {
                display: flex;
                flex-direction: column;
                align-items: center;
                font-weight: 500;
                cursor: pointer;
                transition: all .4s ease-in-out;
                color: wheat;
                padding: .5rem;
                position: relative;

                i {
                    color: Wheat;
                    font-size: 1.4rem;
                    transition: all .4s ease-in-out;
                }

                span {
                    font-size: 0.8rem;
                    margin-top: .2rem;
                }

                &.active {
                    color: White !important;

                    i {
                        color: white !important;
                    }
                }
            }
        }
    }
`;

const SignOut = styled.div`
    display: flex;
    align-items: center;
    cursor: pointer;
    font-weight: 500;
    color: Wheat;
`;

const Footer = styled.div`
    position: fixed;
    bottom: 0;
    left: 0;
    right: 0;
    width: 100%;
    max-width: 500px; /* Adjust this as needed */
    background: black;
    border: 3px solid #FFFFFF;
    border-radius: 32px 32px 32px 32px; /* Rounded top corners */
    box-shadow: 0px -1px 17px rgba(0, 0, 0, 0.06); /* Shadow for floating effect */
    display: flex;
    justify-content: space-around;
    padding: 1rem;
    z-index: 1000;
    margin: 0 auto; /* Center it horizontally */
    @media (max-width: 768px) {
        max-width: 100%; /* Full width on smaller screens */
    }

    .menu-items {
        display: flex;
        justify-content: space-around;
        padding: 0;
        margin: 0;
        list-style: none;

        li {
            display: flex;
            flex-direction: column;
            align-items: center;
            font-weight: 500;
            cursor: pointer;
            transition: all .4s ease-in-out;
            color: wheat;
            padding: .5rem;
            position: relative;

            i {
                color: Wheat;
                font-size: 1.4rem;
                transition: all .4s ease-in-out;
            }

            span {
                font-size: 0.8rem;
                margin-top: .2rem;
            }

            &.active {
                color: White !important;

                i {
                    color: white !important;
                }
            }
        }
    }
`;

export default Navigation;
