import React, { useContext, useState } from "react";
import axios from 'axios';


const GlobalContext = React.createContext();

export const GlobalProvider = ({ children }) => {
    const [incomes, setIncomes] = useState([]);
    const [expenses, setExpenses] = useState([]);
    const [error, setError] = useState(null);
    const [isAuthenticated, setIsAuthenticated] = useState(false); // Track authentication state

    const handleError = (err) => {
        console.error('API Error:', err);
        setError(err.response?.data?.message || 'An error occurred');
    };

    const clearError = () => {
        setError(null);
    };

    const fetchData = async (endpoint, setter) => {
        try {
            const response = await axios.get(`${BASE_URL}${endpoint}`);
            setter(response.data);
        } catch (err) {
            handleError(err);
        }
    };

    const postData = async (endpoint, data, fetchFunction) => {
        try {
            await axios.post(`${BASE_URL}${endpoint}`, data);
            fetchFunction();
            clearError();
        } catch (err) {
            handleError(err);
        }
    };

    const deleteData = async (endpoint, fetchFunction) => {
        try {
            await axios.delete(`${BASE_URL}${endpoint}`);
            fetchFunction();
        } catch (err) {
            handleError(err);
        }
    };

    const addIncome = (income) => postData('add-income', income, getIncomes);
    const getIncomes = () => fetchData('get-incomes', setIncomes);
    const deleteIncome = (id) => deleteData(`delete-income/${id}`, getIncomes);

    const addExpense = (expense) => postData('add-expense', expense, getExpenses);
    const getExpenses = () => fetchData('get-expenses', setExpenses);
    const deleteExpense = (id) => deleteData(`delete-expense/${id}`, getExpenses);

    const totalIncome = () => incomes.reduce((total, income) => total + Number(income.amount), 0);
    const totalExpenses = () => expenses.reduce((total, expense) => total + Number(expense.amount), 0);
    const totalBalance = () => totalIncome() - totalExpenses();

    const transactionHistory = () => {
        const history = [...incomes, ...expenses];
        history.sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt));
        return history.slice(0, 3);
    };

    const signIn = () => setIsAuthenticated(true);
    const signOut = () => setIsAuthenticated(false);

    return (
        <GlobalContext.Provider value={{
            addIncome,
            getIncomes,
            incomes,
            deleteIncome,
            expenses,
            totalIncome,
            addExpense,
            getExpenses,
            deleteExpense,
            totalExpenses,
            totalBalance,
            transactionHistory,
            error,
            setError,
            isAuthenticated, // Provide authentication state
            signIn,         // Provide sign-in function
            signOut         // Provide sign-out function
        }}>
            {children}
        </GlobalContext.Provider>
    );
};

export const useGlobalContext = () => {
    return useContext(GlobalContext);
};
