import React from 'react';
import styled from 'styled-components';

const Modal = ({ isOpen, onClose, children }) => {
  if (!isOpen) return null;

  return (
    <ModalOverlay>
        <ModalContent>
            <CloseButton onClick={onClose}>{Delete}</CloseButton>
            {children} {/* This renders the content passed inside the modal */}
        </ModalContent>
    </ModalOverlay>
);
};

const Overlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
  backdrop-filter: blur(5px);
  z-index: 1000; /* Ensure it's on top of other content */
`;

const ModalContent = styled.div`
  background: #733920;
  padding: 2rem;
  border-radius: 8px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  width: 80%;
  max-width: 500px;
  opacity:0.9;

  /* Customize text color */
  color: wheat; /* Adjust text color here */
`;


const CloseButton = styled.button`
    position: absolute;
    top: 10px;
    right: 10px;
    background: transparent;
    border: none;
    cursor: pointer;
`;

export default Modal;
