import React, { useState } from 'react';
import {
    Chart as ChartJs, 
    ArcElement,
    Tooltip,
    Legend,
} from 'chart.js';
import { Pie } from 'react-chartjs-2';
import styled from 'styled-components';
import { useGlobalContext } from '../../context/globalContext';

ChartJs.register(
    ArcElement,
    Tooltip,
    Legend
);

function IncomeExpensePieChart() {
    const { incomes, expenses } = useGlobalContext();
    const [chartType, setChartType] = useState('income'); // State to track which chart is displayed

    // Prepare income data for the pie chart
    const incomeData = {
        labels: incomes.map((income) => income.category),
        datasets: [
            {
                label: 'Income by Category',
                data: incomes.map((income) => income.amount),
                backgroundColor: ['#4CAF50', '#FFEB3B', '#00BCD4', '#FF5722', '#9C27B0', '#03A9F4', '#8BC34A'],
                borderWidth: 1,
            },
        ],
    };

    // Prepare expense data for the pie chart
    const expenseData = {
        labels: expenses.map((expense) => expense.category),
        datasets: [
            {
                label: 'Expenses by Category',
                data: expenses.map((expense) => expense.amount),
                backgroundColor: ['#4CAF50', '#FFEB3B', '#00BCD4', '#FF5722', '#9C27B0', '#03A9F4', '#8BC34A'],
                borderWidth: 1,
            },
        ],
    };

    return (
        <ChartStyled>
            <ButtonContainer>
                <button onClick={() => setChartType('income')}>Income</button>
                <button onClick={() => setChartType('expense')}>Expenses</button>
            </ButtonContainer>
            <Pie data={chartType === 'income' ? incomeData : expenseData} />
        </ChartStyled>
    );
}

const ChartStyled = styled.div`
    background: #FCF6F9;
    border: 2px solid #FFFFFF;
    box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.06);
    padding: 2rem;
    border-radius: 20px;
    width: 100%;
    max-width: 600px;
    margin: auto;
`;

const ButtonContainer = styled.div`
    display: flex;
    justify-content: center;
    gap: 1rem;
    margin-bottom: 1rem;

    button {
        padding: 0.5rem 1rem;
        border: none;
        border-radius: 5px;
        background-color: #03A9F4;
        color: white;
        cursor: pointer;
        font-size: 1rem;
        transition: background-color 0.3s;

        &:hover {
            background-color: #0288D1;
        }
    }
`;

export default IncomeExpensePieChart;
