import React from 'react';
import styled from 'styled-components';
import { useGlobalContext } from '../context/globalContext';

function History() {
    // Access the recent transactions from the global context
    const { recentTransactions } = useGlobalContext();

    // Get the list of recent transactions
    const history = recentTransactions();

    return (
        <HistoryStyled>
            <h2>Recent Transactions</h2>
            {history.map((item) => {
                const { _id, category, amount, type } = item;

                // Determine if it's an expense or income
                const isExpense = type === 'expense';

                return (
                    <div key={_id} className="history-item">
                        <p
                            style={{
                                color: isExpense ? 'red' : 'var(--color-green)',
                            }}
                        >
                            {category}
                        </p>

                        <p
                            style={{
                                color: isExpense ? 'red' : 'var(--color-green)',
                            }}
                        >
                            {isExpense
                                ? `-${Math.abs(amount).toLocaleString()}` // Show negative value for expenses
                                : `+${amount.toLocaleString()}`}          // Show positive value for income
                        </p>
                    </div>
                );
            })}
        </HistoryStyled>
    );
}

// Styled-component for History section styling
const HistoryStyled = styled.div`
    display: flex;
    flex-direction: column;
    gap: 1rem;
    width: 95%;

    h2 {
        color: wheat;
        z-index: 999;
    }

    .history-item {
        background: #fcf6f9;
        border: 2px solid #ffffff;
        box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.06);
        padding: 1rem;
        border-radius: 20px;
        display: flex;
        justify-content: space-between;
        align-items: center;
        z-index: 999;
    }

    @media (max-width: 900px) { 
        width: 100%;
    }
`;

export default History;
