import React, { useEffect } from 'react';
import styled from 'styled-components';
import { useGlobalContext } from '../../context/globalContext';
import History from '../../History/History';
import { dollar } from '../../utils/Icons';
import Chart from '../Chart/Chart';

function Dashboard() {
    const {
        totalExpenses,
        totalIncome,
        totalBalance,
        getIncomes,
        getExpenses,
        loading,     // Access loading state
        error        // Access error state
    } = useGlobalContext();

    useEffect(() => {
        getIncomes();
        getExpenses();
    }, [getIncomes, getExpenses]); // Include getIncomes and getExpenses in the dependency array

    if (loading) {
        return <LoadingMessage>Loading data...</LoadingMessage>; // Show loading message when fetching data
    }

    if (error) {
        return <ErrorMessage>{error}</ErrorMessage>;  // Display error message if there's an error
    }

    return (
        <DashboardStyled>
            <div className="stats-con">
                <div className="chart-con">
                    <Chart />
                    <div className="amount-con">
                        <div className="income">
                            <h2>Total Income</h2>
                            <p>
                                {dollar} {totalIncome()}
                            </p>
                        </div>
                        <div className="expense">
                            <h2>Total Expense</h2>
                            <p>
                                {dollar} {totalExpenses()}
                            </p>
                        </div>
                        <div className="balance">
                            <h2>Total Balance</h2>
                            <p>
                                {dollar} {totalBalance()}
                            </p>
                        </div>
                    </div>
                </div>
                <div className="history-con">
                    <History />
                </div>
            </div>
        </DashboardStyled>
    );
}

const DashboardStyled = styled.div`
    .stats-con {
        display: flex;
        justify-content: space-between;
        margin-top: 2rem;
    }

    .chart-con {
        width: 65%;
    }

    .amount-con {
        display: flex;
        justify-content: space-around;
        padding: 1rem;
        background-color: #f1f1f1;
        border-radius: 10px;
        margin-top: 1rem;
    }

    .income, .expense, .balance {
        text-align: center;
    }

    .history-con {
        width: 30%;
    }
`;

const LoadingMessage = styled.p`
    text-align: center;
    font-size: 1.5rem;
    color: #666;
`;

const ErrorMessage = styled.p`
    text-align: center;
    color: red;
    font-size: 1.5rem;
`;

export default Dashboard;
