import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import { useGlobalContext } from '../../context/globalContext';
import { InnerLayout } from '../../styles/Layouts';
import IncomeItem from '../IncomeItem/IncomeItem';
import ExpenseForm from './ExpenseForm';
import Modal from '../Modal/Modal'; // Import Modal component

function Expenses() {
    const { expenses, getExpenses, deleteExpense, totalExpenses } = useGlobalContext();
    const [isModalOpen, setIsModalOpen] = useState(false); // State to control modal visibility

    useEffect(() => {
        getExpenses();
    }, [getExpenses]);

    return (
        <ExpenseStyled>
            <InnerLayout>
                <div className="header">
                    <div className="total-expense-container">
                        <h2 className="total-expense">
                            Total Expense: <span>${totalExpenses()}</span>
                        </h2>
                        <button className="add-expense-button" onClick={() => setIsModalOpen(true)}>
                            Add Expense
                        </button>
                    </div>
                </div>
                <Modal isOpen={isModalOpen} onClose={() => setIsModalOpen(false)}>
                    <ExpenseForm />
                </Modal>
                <div className="expenses">
                    {expenses.map((expense) => {
                        const { _id, title, amount, date, category, description, type } = expense;
                        return (
                            <IncomeItem
                                key={_id}
                                id={_id}
                                title={title}
                                description={description}
                                amount={amount}
                                date={date}
                                type={type}
                                category={category}
                                indicatorColor="var(--color-red)" // You can adjust color if needed
                                deleteItem={deleteExpense}
                            />
                        );
                    })}
                </div>
            </InnerLayout>
        </ExpenseStyled>
    );
}

const ExpenseStyled = styled.div`
    display: flex;
    flex-direction: column;
    overflow-y: auto;
    height: 90vh;

    .header {
        display: flex;
        align-items: center;
        justify-content: space-between;
    }

    .total-expense-container {
        display: flex;
        align-items: center;
        gap: 2rem;
        z-index: 998;
    }

    .total-expense {
        color: #222260;
        background: wheat;
        border: 2px solid #FFFFFF;
        box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.06);
        border-radius: 20px;
        padding: 1rem;
        margin: 1rem 0;
        font-size: 1.7rem;
        gap: .5rem;
        span {
            font-size: 2rem;
            font-weight: 800;
            color: var(--color-red); // Adjust color if needed
        }
    }

    .add-expense-button {
        font-size: 1.3rem;
        z-index: 998;
        background-color: var(--color-accent);
        color: white;
        border: none;
        border-radius: 30px;
        padding: 0.8rem 1.6rem;
        cursor: pointer;
        display: flex;
        align-items: center;
        gap: 0.5rem;
        box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.06);
        &:hover {
            background-color: var(--color-red); // Adjust color if needed
        }
    }

    .expenses {
        margin-top: 3rem;
        .month-group {
            border-radius: 10px;
            margin-bottom: 1rem;
            margin-top: 3rem;
            opacity: 1;
            z-index: 1000;
            h3 {
                background: #FCF6F9;
                margin-bottom: 1rem;
                width: fit-content;
                padding: 10px;
                border-radius: 10px;
                color: Black;
                opacity: 1;
            }
        }
    }

    @media (max-width: 700px) {
        .header .total-expense-container {
            display: grid;
            gap: 1rem;
            margin-bottom: 2rem;
        }
    }
`;

export default Expenses;
