// Modal.js
import React from 'react';
import ReactDOM from 'react-dom';
import styled, { keyframes } from 'styled-components'; // Import keyframes

const Modal = ({ showModal, handleClose }) => {
  if (!showModal) return null;

  return ReactDOM.createPortal(
    <>
      <OverlayStyled onClick={handleClose} />
      <ModalStyled>
        <button className="close-button" onClick={handleClose}>X</button>
        {/* Your form or popup content here */}
      </ModalStyled>
    </>,
    document.body
  );
};

const moveOrb = keyframes`
  0% {
    transform: translateX(-50%) rotate(0deg);
  }
  100% {
    transform: translateX(-50%) rotate(360deg);
  }
`;

const OverlayStyled = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.5); /* Dark overlay with transparency */
  backdrop-filter: blur(10px); /* Blur effect */
  z-index: 10; /* Ensure overlay is above main content */
`;

const ModalStyled = styled.div`
  position: fixed;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  background: #fff; /* Modal background */
  border-radius: 10px;
  padding: 2rem;
  width: 80%;
  max-width: 500px;
  z-index: 20; /* Ensure modal is above the overlay */
  animation: ${moveOrb} 15s alternate linear infinite; /* Apply the animation */

  .close-button {
    position: absolute;
    top: 10px;
    right: 10px;
    background: none;
    border: none;
    font-size: 1.5rem;
    cursor: pointer;
  }

  /* Additional styles for your popup content */
`;

export default Modal;
