import React, { useEffect } from 'react';
import styled from 'styled-components';
import { useGlobalContext } from '../../context/globalContext';
import { InnerLayout } from '../../styles/Layouts';
import Form from '../Form/Form';

function Income() {
    const { incomes, getIncomes, deleteIncome, totalIncome } = useGlobalContext();

    useEffect(() => {
        getIncomes();
    }, [getIncomes]);

    // Function to get color based on income type
    const getColorByType = (type) => {
        switch (type) {
            case 'Salary':
                return 'var(--color-green)'; // Salary - green
            case 'Investment':
                return 'var(--color-blue)';  // Investment - blue
            case 'Freelance':
                return 'var(--color-yellow)';  // Freelance - yellow
            default:
                return 'var(--color-gray)';  // Default - gray
        }
    };

    return (
        <IncomeStyled>
            <InnerLayout>
                <h1>Incomes</h1>
                <h2 className="total-income">Total Income: <span>${totalIncome()}</span></h2>
                <div className="income-content">
                    <div className="form-container">
                        <Form />
                    </div>
                    <div className="incomes">
                        {incomes.map((income) => {
                            const { _id, title, amount, date, category, description, type } = income;
                            const incomeColor = getColorByType(type); // Get the color for the income type

                            return (
                                <div className="income-item" key={_id}>
                                    <div className="income-details">
                                        <h3>{title}</h3>
                                        <p>{description}</p>
                                        <p>{date}</p>
                                    </div>
                                    <div className="income-amount">
                                        <p style={{ color: incomeColor }}>${amount}</p> {/* Apply color */}
                                        <span style={{ color: incomeColor }}>{type}</span> {/* Display type with color */}
                                    </div>
                                    <button onClick={() => deleteIncome(_id)}>Delete</button>
                                </div>
                            );
                        })}
                    </div>
                </div>
            </InnerLayout>
        </IncomeStyled>
    );
}

const IncomeStyled = styled.div`
    display: flex;
    flex-direction: column;
    overflow: auto;

    h1 {
        color: wheat;
    }

    .total-income {
        display: flex;
        justify-content: center;
        align-items: center;
        background: #FCF6F9;
        border: 2px solid #FFFFFF;
        box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.06);
        border-radius: 20px;
        padding: 1rem;
        margin: 1rem 0;
        font-size: 2rem;
        gap: 0.5rem;
        span {
            font-size: 2.5rem;
            font-weight: 800;
            color: var(--color-green);
        }
    }

    .income-content {
        display: flex;
        gap: 2rem;

        .incomes {
            flex: 1;

            .income-item {
                background: #fff;
                padding: 1rem;
                border-radius: 20px;
                box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.06);
                margin-bottom: 1rem;
                display: flex;
                justify-content: space-between;
                align-items: center;

                .income-details {
                    h3 {
                        font-size: 1.5rem;
                    }
                    p {
                        font-size: 1rem;
                        margin: 0.2rem 0;
                    }
                }

                .income-amount {
                    p {
                        font-size: 1.5rem;
                        font-weight: bold;
                    }

                    span {
                        font-size: 1rem;
                        font-weight: 500;
                        margin-top: 0.5rem;
                    }
                }

                button {
                    background-color: var(--color-red);
                    color: white;
                    padding: 0.5rem 1rem;
                    border: none;
                    border-radius: 10px;
                    cursor: pointer;

                    &:hover {
                        background-color: darkred;
                    }
                }
            }
        }
    }
`;

export default Income;
