import React from 'react';
import {
    Chart as ChartJs, 
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Legend,
    ArcElement,
} from 'chart.js';
import { Line } from 'react-chartjs-2';
import styled from 'styled-components';
import { useGlobalContext } from '../../context/globalContext';
import { dateFormat } from '../../utils/dateFormat';

ChartJs.register(
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Legend,
    ArcElement,
);

function Chart() {
    const { incomes, expenses } = useGlobalContext();

    const data = {
        labels: incomes.map((inc) => dateFormat(inc.date)),
        datasets: [
            {
                label: 'Income',
                data: incomes.map((income) => income.amount),
                borderColor: 'green',
                backgroundColor: 'rgba(0, 255, 0, 0.2)', // Gradient background
                borderWidth: 2,
                tension: 0.3,
                pointBackgroundColor: 'green',
                pointBorderColor: 'white',
                pointBorderWidth: 2,
                pointRadius: 4,
                fill: true,
            },
            {
                label: 'Expenses',
                data: expenses.map((expense) => expense.amount),
                borderColor: 'red',
                backgroundColor: 'rgba(255, 0, 0, 0.2)', // Gradient background
                borderWidth: 2,
                tension: 0.3,
                pointBackgroundColor: 'red',
                pointBorderColor: 'white',
                pointBorderWidth: 2,
                pointRadius: 4,
                fill: true,
            }
        ]
    };

    const options = {
        responsive: true,
        plugins: {
            legend: {
                position: 'top',
                labels: {
                    color: 'black',
                },
            },
            tooltip: {
                callbacks: {
                    label: function(tooltipItem) {
                        return `${tooltipItem.dataset.label}: $${tooltipItem.raw}`;
                    }
                }
            }
        },
        scales: {
            x: {
                ticks: {
                    color: 'black',
                },
                grid: {
                    color: '#e0e0e0',
                },
            },
            y: {
                ticks: {
                    color: 'black',
                },
                grid: {
                    color: '#e0e0e0',
                },
            },
        },
    };

    return (
        <ChartStyled>
            <Line data={data} options={options} />
        </ChartStyled>
    );
}

const ChartStyled = styled.div`
    background: #FCF6F9;
    border: 2px solid #FFFFFF;
    box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.06);
    padding: 1rem;
    border-radius: 20px;
    height: 100%;
    width: 30rem;
    max-width: 200rem;
    margin: auto;
`;

export default Chart;
