// App.js
import React, { useState, useEffect, useMemo } from 'react';
import styled from 'styled-components';
import bg from './img/backgroundimg.png';
import { MainLayout } from './styles/Layouts.js';
import Orb from '../Components/Orb/Orb.js';
import Navigation from './Components/Navigation/Navigation.js';
import Dashboard from './Components/Dashboard/Dashboard.js';
import Income from './Components/Income/Income.js';
import Expenses from './Components/Expenses/Expenses.js';
import { useGlobalContext } from './context/globalContext';

function App() {
  const [active, setActive] = useState(() => Number(localStorage.getItem('active')) || 1);
  const [loading, setLoading] = useState(true);
  const [data, setData] = useState([]);
  const global = useGlobalContext();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('/api/v1/get-expenses');
        const result = await response.json();
        setData(result);
      } catch (error) {
        console.error('Error fetching data:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  useEffect(() => {
    localStorage.setItem('active', active);
  }, [active]);

  const displayData = () => {
    if (loading) return <div>Loading...</div>;

    switch (active) {
      case 1:
        return <Dashboard data={data} />;
      case 2:
        return <Dashboard data={data} />; // Adjust based on actual content
      case 3:
        return <Income data={data} />;
      case 4:
        return <Expenses data={data} />;
      default:
        return <Dashboard data={data} />;
    }
  };

  const orbMemo = useMemo(() => <Orb />, []);

  return (
    <AppStyled bg={bg} className="App">
      {orbMemo}
      <MainLayout>
        <main>
          {displayData()}
        </main>
      </MainLayout>
      <Navigation active={active} setActive={setActive} />
    </AppStyled>
  );
}

const AppStyled = styled.div`
  background-color: #000000;
  position: relative;
  overflow: auto;
  display: flex;
  flex-direction: column;
  height:100vh;

  main {
    margin-top: 3rem;
    flex-grow: 1; 
    overflow-y: auto;  
    &::-webkit-scrollbar {
      width: 0;
    }
  }
`;

export default App;
