import React from 'react';
import { Delete } from '../../utils/Icons'; // Import the close icon
import styled from 'styled-components';

function Modal({ isOpen, onClose, children }) {
  if (!isOpen) return null;

  return (
    <ModalOverlay>
      <ModalContent>
        <CloseButton onClick={onClose}>{Delete}</CloseButton>
        {children} {/* This renders the content passed in the Modal */}
      </ModalContent>
    </ModalOverlay>
  );
}

const ModalOverlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.5);  /* Blur effect */
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 1000;
  backdrop-filter: blur(5px);  /* Apply blur to the background */
`;

const ModalContent = styled.div`
  background: linear-gradient(180deg, #F56692 100%, #F2994A 100%);
  padding: 2rem;
  border-radius: 10px;
  position: relative;
  z-index: 1001;
`;

const CloseButton = styled.button`
  position: absolute;
  top: 10px;
  right: 10px;
  background: transparent;
  border: none;
  cursor: pointer;
`;

export default Modal;
