import React, { useState, useEffect, useMemo, useContext } from 'react';
import styled from 'styled-components';
import bg from './img/backgroundimg.png';
import { MainLayout } from './styles/Layouts.js';
import Orb from './Components/Orb/Orb.js';
import Navigation from './Components/Navigation/Navigation.js';
import Dashboard from './Components/Dashboard/Dashboard.js';
import Income from './Components/Income/Income.js';
import Expenses from './Components/Expenses/Expenses.js';
import { useGlobalContext } from './context/globalContext';
import SignUp from './Components/SignUp/SignUp';
import Login from './Components/Login/Login';
import SignOut from './Components/SignOut/SignOut';
import { AuthContext } from './context/AuthContext'; // Auth Context for global auth state
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom'; // Updated for v6

function App() {
  const [active, setActive] = useState(() => {
    // Retrieve the active state from localStorage or default to 1
    return Number(localStorage.getItem('active')) || 1;
  });
  const [loading, setLoading] = useState(true);
  const [data, setData] = useState([]);
  const global = useGlobalContext();
  const { auth, setAuth } = useContext(AuthContext);  // Access auth state

  console.log(global);

  // Fetch data when the component mounts
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('/api/v1/get-expenses');
        const result = await response.json();
        setData(result);
      } catch (error) {
        console.error('Error fetching data:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  // Save active state to localStorage when it changes
  useEffect(() => {
    localStorage.setItem('active', active);
  }, [active]);

  const displayData = () => {
    if (loading) return <div>Loading...</div>;

    switch (active) {
      case 1:
        return <Dashboard data={data} />;
      case 2:
        return <Dashboard data={data} />; // Adjust based on actual content
      case 3:
        return <Income data={data} />;
      case 4:
        return <Expenses data={data} />;
      default:
        return <Dashboard data={data} />;
    }
  };

  const orbMemo = useMemo(() => <Orb />, []);

  // return (
  //   <Router>
  //     <AppStyled bg={bg} className="App">
  //       {orbMemo}
  //       <MainLayout>
  //         <Routes>
  //           {/* Routes for authentication */}
  //           <Route path="/signup" element={<SignUp />} />
  //           <Route path="/login" element={<Login setAuth={setAuth} />} />
  //           {/* Authenticated routes */}
  //           <Route path="/" element={<main>{displayData()}</main>} />
  //           <Route path="/signout" element={<SignOut setAuth={setAuth} />} />
  //           <Route path="*" element={<Navigate to={auth ? "/" : "/login"} />} />
  //         </Routes>
  //       </MainLayout>
  //       {auth && <Navigation active={active} setActive={setActive} />}
  //     </AppStyled>
  //   </Router>
  // );
}

const AppStyled = styled.div`
  background-color: #000000;
  position: relative;
  overflow: auto;
  display: flex;
  flex-direction: column;
  height: 100vh;

  main {
    margin-top: 3rem;
    flex-grow: 1; 
    overflow-y: auto;  
    &::-webkit-scrollbar {
      width: 0;
    }
  }
`;

export default App;
