import React, { useEffect } from 'react'
import styled from 'styled-components'
import { useGlobalContext } from '../../context/globalContext';
import History from '../../History/History';
import { dollar } from '../../utils/Icons';
import Chart from '../Chart/Chart';

function Dashboard() {
    const { totalExpenses, totalIncome, totalBalance, getIncomes, getExpenses } = useGlobalContext();

    useEffect(() => {
        getIncomes();
        getExpenses();
    }, [getIncomes, getExpenses]);

    return (
        <DashboardStyled>
            <div className="chart-container">
                <h1>Spending Report</h1>
                <Chart />
                <div className="balance">
                    <h2>Remaining Balance</h2>
                    <p>{dollar} {totalBalance()}</p>
                </div>
            </div>
            <div className="stats-con">
                <div className="chart-con">
                    <div className="amount-con">
                        <div className="income">
                            <h2>Total Income</h2>
                            <p>{dollar} {totalIncome()}</p>
                        </div>
                        <div className="expense">
                            <h2>Total Expense</h2>
                            <p>{dollar} {totalExpenses()}</p>
                        </div>
                    </div>
                </div>
                <div className="history-con">
                    <History />
                </div>
            </div>
        </DashboardStyled>
    );
}

const DashboardStyled = styled.div`
    display: flex;
    justify-content: space-between;
    align-items: center;
    gap: 2rem;
    padding: 2rem 1.5rem;

    .chart-container {
        display: grid;
        justify-content: center;
        align-items: center;
        width: 100%;
        h1{
            color: wheat;
            text-align:center;
            z-index:999;
        }
        .balance{
            background: white;
            margin-top:3rem;
            border: 2px solid #FFFFFF;
            box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.06);
            border-radius: 20px;
            padding: 1rem;
            z-index:999;
            text-wrap: nowrap;

            h2 {
                font-size: 2rem;
                font-weight: bold;
            }

            p {
                font-weight: 600;
                font-size: 1.5rem;
                color: var(--color-green);
            }
        }
    }

    .stats-con {
        display: flex;
        flex-direction: column;
        gap: 2rem;

        .chart-con {
            /* Adjust height to take only the required space */
            .amount-con {
                display: grid;
                grid-template-columns: repeat(3, 1fr);
                gap: 2rem;
                margin-top: 2rem;

                .income, .expense{
                    background: #FCF6F9;
                    border: 2px solid #FFFFFF;
                    box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.06);
                    border-radius: 20px;
                    padding: 1rem;
                    z-index:999;
                    text-wrap: nowrap;

                    h2 {
                        font-size: 2rem;
                        font-weight: bold;
                    }

                    p {
                        font-weight: 600;
                        font-size: 1.5rem;
                    }
                }
            }
        }

        .history-con {
            /* Adjust to fill remaining space below chart */
            h2 {
                margin: 1rem 0;
                display: flex;
                align-items: center;
                justify-content: space-between;
            }
        }
    }

    @media (max-width: 768px) { 
        flex-direction: column;
        justify-content: center;
        align-items: center;
        overflow-y: auto;
        margin: 0 auto;

    .chart-container, .stats-con {
        width: 100%; 
    }

    .chart-container {
        margin-bottom: 2rem; 
    }

    @media (max-width: 500px) { 
        margin: 0 auto;
    }
`;

export default Dashboard;
