import React, { useState } from 'react';
import styled from 'styled-components';
import maleIcon from '../../img/male.png';
import femaleIcon from '../../img/female.png';
import { signout, burgerIcon } from '../../utils/Icons';
import { menuItems } from '../../utils/menuItems';
import { useGlobalContext } from '../../context/globalContext';

function Navigation({ active, setActive }) {
    const [menuOpen, setMenuOpen] = useState(false);
    const [isModalOpen, setIsModalOpen] = useState(false); // State to manage modal visibility
    const { userPreferences, updateUserPreferences } = useGlobalContext();

    const handleMenuItemClick = (id) => {
        setActive(id);
        setMenuOpen(false);
    };

    const handleNameChange = (e) => {
        updateUserPreferences({ name: e.target.value });
    };

    const handleAvatarChange = (e) => {
        updateUserPreferences({ avatar: e.target.value });
    };

    return (
        <Header>
            <UserInfo>
                <img
                    src={userPreferences.avatar === 'male' ? maleIcon : femaleIcon}
                    alt="User Avatar"
                />
                <div className="text">
                    <h2>{userPreferences.name}</h2>
                    <EditIcon onClick={() => setIsModalOpen(true)}>✎</EditIcon> {/* Edit icon */}
                </div>
            </UserInfo>
            <BurgerIcon onClick={() => setMenuOpen(!menuOpen)}>
                {burgerIcon}
            </BurgerIcon>
            <Menu className={menuOpen ? 'open' : ''}>
                {menuItems.map((item) => (
                    <li
                        key={item.id}
                        onClick={() => handleMenuItemClick(item.id)}
                        className={active === item.id ? 'active' : ''}
                    >
                        {item.icon}
                        <span>{item.title}</span>
                    </li>
                ))}
            </Menu>
            <SignOut>
                {signout} Sign Out
            </SignOut>

            {/* Modal for editing name and avatar */}
            {isModalOpen && (
                <Modal>
                    <ModalContent>
                        <CloseButton onClick={() => setIsModalOpen(false)}>✕</CloseButton>
                        <h2>Edit Profile</h2>
                        <label>
                            Name:
                            <input
                                type="text"
                                value={userPreferences.name}
                                onChange={handleNameChange}
                                placeholder="Enter your name"
                            />
                        </label>
                        <label>
                            Avatar:
                            <select value={userPreferences.avatar} onChange={handleAvatarChange}>
                                <option value="male">Male</option>
                                <option value="female">Female</option>
                            </select>
                        </label>
                        <button onClick={() => setIsModalOpen(false)}>Save</button>
                    </ModalContent>
                </Modal>
            )}
        </Header>
    );
}

const Header = styled.div`
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    background: black;
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 0 2rem;
    box-shadow: 0px 1px 17px rgba(0, 0, 0, 0.06);
    z-index: 1000;
    height: 80px; /* Adjust as needed */

    @media (max-width: 768px) {
        padding: 0 1rem;
    }
`;

const UserInfo = styled.div`
    display: flex;
    align-items: center;
    gap: 1rem;

    img {
        width: 50px;
        height: 50px;
        border-radius: 50%;
        object-fit: cover;
        background: #fcf6f9;
        border: 2px solid #FFFFFF;
        padding: .2rem;
        box-shadow: 0px 1px 17px rgba(0, 0, 0, 0.06);
    }

    .text {
        color: white;

        h2 {
            color: Wheat;
            margin: 0;
        }

        position: relative;
    }
`;

const EditIcon = styled.span`
    font-size: 1rem;
    cursor: pointer;
    position: absolute;
    top: 0;
    right: -20px;
    color: Wheat;

    &:hover {
        color: White;
    }
`;

const Menu = styled.ul`
    display: flex;
    gap: 2rem;
    transition: all 0.3s ease;

    li {
        display: flex;
        flex-direction: column;
        align-items: center;
        font-weight: 500;
        cursor: pointer;
        transition: all .4s ease-in-out;
        color: wheat;
        padding: .5rem;
        position: relative;

        i {
            color: Wheat;
            font-size: 1.4rem;
            transition: all .4s ease-in-out;
        }

        span {
            font-size: 0.8rem;
            margin-top: .2rem;
        }

        &.active {
            color: White !important;

            i {
                color: white !important;
            }
        }
    }

    @media (max-width: 768px) {
        display: ${props => props.className === 'open' ? 'flex' : 'none'};
        flex-direction: column;
        position: absolute;
        top: 80px;
        left: 0;
        width: 100%;
        background: black;
        padding: 1rem;
    }
`;

const SignOut = styled.div`
    display: flex;
    align-items: center;
    cursor: pointer;
    font-weight: 500;
    color: Wheat;

    @media (max-width: 768px) {
        // display: none;
    }
`;

const BurgerIcon = styled.div`
    display: none;
    cursor: pointer;
    color: white;
    font-size: 1.5rem;

    @media (max-width: 768px) {
        display: block;
    }
`;

/* Modal Styles */
const Modal = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
  backdrop-filter: blur(5px);
  z-index: 1000; /* Ensure it's on top of other content */
`;

const ModalContent = styled.div`
    background: white;
    padding: 2rem;
    border-radius: 10px;
    text-align: center;
    position: relative;

    h2 {
        margin-bottom: 1rem;
        font-size:2rem;
    }

    label {
        display: flex;
        flex-direction: column;
        font-size:1rem;
        gap: 0.5rem;
        margin-bottom: 1rem;

        input, select {
            padding: 0.5rem;
            border: 1px solid gray;
        }
    }

    button {
        padding: 0.5rem 1rem;
        background: var(--color-accent) ;
        color: white;
        border: none;
        cursor: pointer;
        border-radius:15px;
        padding: 10 40px;

        &:hover {
            background: var(--color-green);
        }
    }
`;

const CloseButton = styled.span`
    position: absolute;
    top: 10px;
    right: 10px;
    cursor: pointer;
    font-size: 1.5rem;
    color: black;

    &:hover {
        color: red;
    }
`;

export default Navigation;
