import React, { useState } from 'react';
import styled from 'styled-components';
import Modal from '../Modal/Modal';
import Button from '../Button/Button';
import { plus } from '../../utils/Icons';

function Form() {
    const [showModal, setShowModal] = useState(false);

    const handleAddIncomeClick = () => {
        setShowModal(true);
        document.body.classList.add('modal-open');  // Apply blur effect to the background
    };

    const handleCloseModal = () => {
        setShowModal(false);
        document.body.classList.remove('modal-open');  // Remove blur effect
    };

    return (
        <FormStyled>
            <Button
                name={'Add Income'}
                icon={plus}
                bPad={'.8rem 1.6rem'}
                bRad={'30px'}
                bg={'var(--color-accent)'}
                color={'#fff'}
                onClick={handleAddIncomeClick}
            />
            <Modal showModal={showModal} setShowModal={handleCloseModal}>
                {/* Add your income form here */}
                <h3>Add New Income</h3>
                {/* Include other form fields */}
            </Modal>
        </FormStyled>
    );
}

const FormStyled = styled.div`
  display: flex;
  flex-direction: column;
  gap: 2rem;
  input, textarea, select {
    font-family: inherit;
    font-size: inherit;
    outline: none;
    border: none;
    padding: .5rem 1rem;
    border-radius: 5px;
    border: 2px solid #ddd; /* Adjust form input border color */
    background: #f8f8f8; /* Adjust form input background color */
    color: black; /* Adjust form text color */
    &::placeholder {
      color: Black; /* Adjust placeholder text color */
    }
  }
  .input-control {
    input {
      width: 100%;
    }
  }
  .selects {
    display: flex;
    justify-content: flex-end;
    select {
      color: #333; /* Adjust select text color */
    }
    select:focus {
      color: #222; /* Adjust select focus text color */
    }
  }
  .submit-btn {
    button {
      box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.06);
      &:hover {
        background: #4caf50; /* Adjust button hover background color */
      }
    }
  }
`;

export default Form;
